pragma solidity ^0.4.24;

import "zeppelin-solidity/contracts/ownership/Ownable.sol";

contract PolicyManager is Ownable {

    uint totalPolicies = 0;

    struct Policy {
        string name;
        string coverages;
        address owner;
        uint created;
    }

    Policy[] public policies;

    function createPolicy (string name, string coverages) public {
        
        policies.push(Policy(name, coverages, msg.sender, now));
        totalPolicies++;
    }
}
